def NumberConvert(n):
    return n.replace('0','ฐ').replace('1','ฑ').replace('2','ฒ').replace('3','ณ').replace('4','ด').replace('5','ต').replace('6','ถ').replace('7','ท').replace('8','ธ').replace('9','น')

