_getArticle=['',datetime.datetime.now()]
def Article(selector):
    if _getArticle[0]=='' or datetime.datetime.now()-datetime.timedelta(hours=1)>_getArticle[1]:
        try:
            req=requests.get('https://corona.ir/')
            _article=''
            if req.status_code==200:
                soup = BeautifulSoup(req.content, 'html.parser')
                soup=soup.select_one(selector)
                articles=soup.findAll('h4')
                for a in articles:
                    _article+=a.get_text()+'\nhttps://corona.ir'+a.find('a')['href']+'\n'
            _getArticle[0]=_article
            _getArticle[1]=datetime.datetime.now()
        except Exception as e:
            print(str(e))
    return _getArticle[0]

